import React, { useState, useEffect } from "react";
import {
  Box,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Container,
  Drawer,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Avatar,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import PlayCircleFilledWhiteIcon from "@mui/icons-material/PlayCircleFilledWhite";
import { motion } from "framer-motion";

export default function CoursePage() {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [visibleCount, setVisibleCount] = useState(3);

  // ✅ همه‌ی درس‌ها (داده ثابت)
  const allLessons = [
    {
      title: "مقدمه و آشنایی با دوره",
      duration: "5 دقیقه",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTSUsQ0YKoF8dxL932geP4zLa5CCbRgpFsNDA&s",
      author: "علی احمدی",
    },
    {
      title: "نصب ابزارها و محیط توسعه",
      duration: "12 دقیقه",
      image: "data:image/png;base64,",
      author: "علی احمدی",
    },
    {
      title: "مبانی جاوااسکریپت برای ریاکت",
      duration: "20 دقیقه",
      image: "data:image/png;base64,",
      author: "علی احمدی",
    },
    {
      title: "ساخت اولین کامپوننت",
      duration: "15 دقیقه",
      image: "data:image/jpeg;base64,",
      author: "علی احمدی",
    },
    {
      title: "مدیریت State و Props",
      duration: "18 دقیقه",
      image: "data:image/jpeg;base64,",
      author: "علی احمدی",
    },
    {
      title: "کار با Hooks پیشرفته",
      duration: "22 دقیقه",
      image: "data:image/jpeg;base64,",
      author: "علی احمدی",
    },
  ];

  const drawerItems = ["خانه", "دوره‌ها", "درباره ما", "تماس"];

  // ✅ هندل اسکرول برای lazy load
  useEffect(() => {
    const handleScroll = () => {
      const isBottom =
        window.innerHeight + window.scrollY >= document.body.scrollHeight - 100;
      if (isBottom && visibleCount < allLessons.length) {
        setVisibleCount((prev) => prev + 2);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [visibleCount, allLessons.length]);

  // ✅ انیمیشن‌ها
  const containerVariants = {
    hidden: {},
    visible: { transition: { staggerChildren: 0.2 } },
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 40 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
  };

  return (
    <Box sx={{ bgcolor: "#f9fafc", minHeight: "100vh" }}>
      {/* Navigation */}
      <Box
        sx={{
          width: "97%",
          bgcolor: "#1976d2",
          color: "#fff",
          py: 2,
          px: 3,
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          position: "sticky",
          top: 0,
          zIndex: 999,
          boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
        }}
      >
        <Typography variant="h6" sx={{ fontWeight: "bold" }}>
          پی سی لرن
        </Typography>
        <IconButton color="inherit" onClick={() => setDrawerOpen(true)}>
          <MenuIcon />
        </IconButton>
      </Box>

      {/* Drawer */}
      <Drawer anchor="right" open={drawerOpen} onClose={() => setDrawerOpen(false)}>
        <Box sx={{ width: 250 }}>
          <List>
            {drawerItems.map((text, index) => (
              <ListItem button key={index}>
                <ListItemText primary={text} />
              </ListItem>
            ))}
          </List>
        </Box>
      </Drawer>

      {/* Course Cards */}
      <Container maxWidth="md" sx={{ mt: 4, pb: 5 }}>
        <motion.div variants={containerVariants} initial="hidden" animate="visible">
          {allLessons.slice(0, visibleCount).map((lesson, index) => (
            <motion.div variants={cardVariants} key={index}>
              <Card
                sx={{
                  display: "flex",
                  alignItems: "center",
                  mb: 4,
                  borderRadius: 3,
                  overflow: "hidden",
                  boxShadow: "0 8px 24px rgba(0,0,0,0.1)",
                  transition: "all 0.3s ease",
                  "&:hover": {
                    transform: "translateY(-6px)",
                    boxShadow: "0 12px 32px rgba(0,0,0,0.2)",
                  },
                }}
              >
                <CardMedia
                  component="img"
                  sx={{ width: 150, height: 100 }}
                  image={lesson.image}
                  alt={lesson.title}
                />
                <CardContent sx={{ flex: 1 }}>
                  <Typography variant="h6" sx={{ fontWeight: "bold" }}>
                    {lesson.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5 }}>
                    مدرس: {lesson.author} — زمان: {lesson.duration}
                  </Typography>
                  <Button
                    variant="contained"
                    startIcon={<PlayCircleFilledWhiteIcon />}
                    sx={{
                      mt: 2,
                      bgcolor: "#1976d2",
                      borderRadius: "25px",
                      textTransform: "none",
                      px: 3,
                      py: 1,
                      "&:hover": { boxShadow: "0 0 15px rgba(25,118,210,0.5)" },
                    }}
                  >
                    شروع درس
                  </Button>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </motion.div>

        {visibleCount >= allLessons.length && (
          <Box sx={{ textAlign: "center", mt: 3 }}>
            <Typography color="text.secondary">گروه پی سی لرن</Typography>
          </Box>
        )}
      </Container>
    </Box>
  );
}
